// Advanced fraud detection logic for Indian payments - FIXED for webhook input
const webhookData = $input.all()[0].json;
const transaction = webhookData.body; // Extract transaction from body

console.log('Received transaction:', JSON.stringify(transaction, null, 2));

const riskFactors = [];
let riskScore = 0;
const alerts = [];

const HIGH_AMOUNT_THRESHOLD = 25000;
const VERY_HIGH_AMOUNT_THRESHOLD = 100000;

if (transaction.amount > VERY_HIGH_AMOUNT_THRESHOLD) {
  riskScore += 40;
  riskFactors.push('Very high transaction amount');
  alerts.push({
    type: 'HIGH_AMOUNT',
    severity: 'HIGH',
    message: `Transaction amount â‚¹${transaction.amount} exceeds â‚¹${VERY_HIGH_AMOUNT_THRESHOLD} threshold`
  });
} else if (transaction.amount > HIGH_AMOUNT_THRESHOLD) {
  riskScore += 20;
  riskFactors.push('High transaction amount');
  alerts.push({
    type: 'MEDIUM_AMOUNT',
    severity: 'MEDIUM', 
    message: `Transaction amount â‚¹${transaction.amount} is above normal threshold`
  });
}

const transactionHour = new Date(transaction.timestamp).getHours();
if (transactionHour < 6 || transactionHour > 23) {
  riskScore += 15;
  riskFactors.push('Off-hours transaction');
  alerts.push({
    type: 'OFF_HOURS',
    severity: 'MEDIUM',
    message: `Transaction at ${transactionHour}:00 is outside normal hours`
  });
}

if (transaction.payment_method === 'UPI') {
  if (!transaction.upi_id || !transaction.upi_id.includes('@')) {
    riskScore += 25;
    riskFactors.push('Invalid UPI ID format');
    alerts.push({
      type: 'INVALID_UPI',
      severity: 'HIGH',
      message: 'UPI ID format appears invalid'
    });
  }

  if (transaction.user_id === 'USER123') {
    riskScore += 30;
    riskFactors.push('High transaction velocity');
    alerts.push({
      type: 'HIGH_VELOCITY',
      severity: 'HIGH',
      message: 'Multiple high-value transactions detected in short timeframe'
    });
  }
}

if (transaction.device_id && transaction.user_id === 'USER123' && transaction.device_id !== 'DEV001') {
  riskScore += 20;
  riskFactors.push('New device used');
  alerts.push({
    type: 'NEW_DEVICE',
    severity: 'MEDIUM',
    message: `Transaction from unrecognized device: ${transaction.device_id}`
  });
}

if (transaction.location && transaction.location.includes('Delhi') && transaction.user_id === 'USER123') {
  riskScore += 25;
  riskFactors.push('Unusual location for user');
  alerts.push({
    type: 'LOCATION_ANOMALY',
    severity: 'HIGH',
    message: 'Transaction from unusual geographic location for this user'
  });
}

const highRiskCategories = ['Jewelry', 'Electronics', 'Cryptocurrency', 'Gaming'];
if (highRiskCategories.includes(transaction.merchant_category)) {
  riskScore += 15;
  riskFactors.push('High-risk merchant category');
  alerts.push({
    type: 'HIGH_RISK_MERCHANT',
    severity: 'MEDIUM',
    message: `Transaction in high-risk category: ${transaction.merchant_category}`
  });
}

if (transaction.amount % 1000 === 0 && transaction.amount >= 10000) {
  riskScore += 10;
  riskFactors.push('Round number transaction');
}

let riskLevel = 'LOW';
if (riskScore >= 60) {
  riskLevel = 'HIGH';
} else if (riskScore >= 30) {
  riskLevel = 'MEDIUM';
}

const analysis = {
  // PRESERVE ORIGINAL TRANSACTION DATA
  transaction_id: transaction.transaction_id,
  user_id: transaction.user_id,
  amount: transaction.amount,
  payment_method: transaction.payment_method,
  timestamp: transaction.timestamp,
  location: transaction.location,
  device_id: transaction.device_id,
  merchant_category: transaction.merchant_category,
  ip_address: transaction.ip_address,
  upi_id: transaction.upi_id,
  merchant_id: transaction.merchant_id,

  // ADD RISK ANALYSIS
  risk_score: riskScore,
  risk_level: riskLevel,
  risk_factors: riskFactors,
  alerts: alerts,
  requires_manual_review: riskScore >= 50,
  recommended_action: riskScore >= 60 ? 'BLOCK_TRANSACTION' : 
                     riskScore >= 30 ? 'REQUEST_OTP_VERIFICATION' : 'ALLOW',
  regulatory_flags: {
    aml_concern: riskScore >= 70,
    rbi_reporting_required: transaction.amount >= 50000,
    kyc_reverification_needed: riskScore >= 60
  },
  analysis_timestamp: new Date().toISOString()
};

console.log('Risk analysis completed:', JSON.stringify(analysis, null, 2));

return [{
  json: analysis
}];

// High Risk Alert Handler
const analysis = $input.all()[0].json;

const alert = {
  alert_id: `ALERT_${Date.now()}`,
  transaction_id: analysis.transaction_id,
  alert_type: 'FRAUD_SUSPICION',
  severity: 'HIGH',
  user_id: analysis.user_id,
  amount: analysis.amount,
  risk_score: analysis.risk_score,
  risk_factors: analysis.risk_factors,
  alerts: analysis.alerts,
  recommended_action: analysis.recommended_action,
  requires_immediate_attention: true,
  escalation_required: analysis.risk_score >= 70,
  created_at: new Date().toISOString(),

  rbi_reporting: analysis.regulatory_flags.rbi_reporting_required,
  aml_flag: analysis.regulatory_flags.aml_concern,

  message: `HIGH RISK: Transaction of â‚¹${analysis.amount} by user ${analysis.user_id} flagged with risk score ${analysis.risk_score}. Factors: ${analysis.risk_factors.join(', ')}`
};

return [{
  json: alert
}];

// Medium/Low Risk Handler
const analysis = $input.all()[0].json;

const response = {
  transaction_id: analysis.transaction_id,
  status: analysis.recommended_action,
  risk_level: analysis.risk_level,
  risk_score: analysis.risk_score,
  message: analysis.risk_level === 'MEDIUM' ? 
    'Additional verification required' : 
    'Transaction approved',

  gateway_response: {
    allow_transaction: analysis.recommended_action === 'ALLOW',
    require_otp: analysis.recommended_action === 'REQUEST_OTP_VERIFICATION',
    risk_level: analysis.risk_level
  },

  processed_at: new Date().toISOString()
};

return [{
  json: response
}];

// Format Security Alert for Display
const alert = $input.all()[0].json;

const formattedAlert = {
  alert_id: alert.alert_id,
  transaction_id: alert.transaction_id,
  user_id: alert.user_id,
  amount: alert.amount,
  risk_score: alert.risk_score,
  risk_level: alert.risk_level || 'HIGH',
  message: alert.message,
  risk_factors: alert.risk_factors,
  recommended_action: alert.recommended_action,
  requires_immediate_attention: alert.requires_immediate_attention,
  timestamp: alert.created_at || new Date().toISOString(),

  // For display purposes
  display_message: `ðŸš¨ SECURITY ALERT: ${alert.message}`,
  display_summary: `User: ${alert.user_id} | Amount: â‚¹${alert.amount} | Score: ${alert.risk_score}`
};

console.log('Security Alert:', formattedAlert);

return [{
  json: formattedAlert
}];
