// Helper function to ensure URLs have proper protocol
function ensureHttps(url) {
    if (!url) return '';
    if (url.startsWith('http')) return url;
    return 'https:' + url;
}

// Helper function to get current date
function getCurrentDate() {
    const options = { 
        weekday: 'long', 
        year: 'numeric', 
        month: 'long', 
        day: 'numeric' 
    };
    return new Date().toLocaleDateString('en-US', options);
}

// Get all input items
const allItems = $input.all();

// Filter articles where Rock_News is true
const rockArticles = allItems.filter(item => item.json.Rock_News === true);

// Generate article HTML
let articlesHTML = '';

if (rockArticles.length === 0) {
    articlesHTML = `
        <div style="text-align: center; padding: 40px; color: #666;">
            <p>No rock news articles available today. Check back tomorrow for fresh updates!</p>
        </div>
    `;
} else {
    articlesHTML = rockArticles.map((item, index) => {
        const article = item.json;

        const imageHTML = article.article_image_url ? `
            <img src="${ensureHttps(article.article_image_url)}" 
                 alt="${article.article_image_alt || article.article_title}" 
                 class="article-image">
        ` : '';

        const dividerHTML = index < rockArticles.length - 1 ? '<div class="divider"></div>' : '';

        return `
            <article class="article">
                ${imageHTML}

                <h2 class="article-title">
                    <a href="${ensureHttps(article.article_url)}" target="_blank">
                        ${article.article_title}
                    </a>
                </h2>

                <p class="article-excerpt">
                    ${article.article_excerpt}
                </p>

                <a href="${ensureHttps(article.article_url)}" 
                   class="read-more" target="_blank">Read Full Story</a>
            </article>

            ${dividerHTML}
        `;
    }).join('');
}

